declare module "*.scss" {
  const content: { [className: string]: string };
  export default content;
}

declare module '*.jpg' {
  const src: string
  export default src
}

declare module '*.png' {
  const src: string
  export default src
}

declare module '*webp' {
  const src: string
  export default src
}

declare module '*mp4' {
  const src: string
  export default src
}

declare module '*webm' {
  const src: string
  export default src
}
